import React, { useState } from 'react';

const Test = ({ selectedQuestions, testData, onShowResult }) => {
  const [answers, setAnswers] = useState({});
  const [testCompleted, setTestCompleted] = useState(false);
  const [correctCount, setCorrectCount] = useState(0);

  const handleAnswerSelect = (questionIndex, answerIndex) => {
    setAnswers(prevAnswers => ({
      ...prevAnswers,
      [questionIndex]: answerIndex,
    }));
  };

  const handleNextPage = () => {
    if (Object.keys(answers).length < selectedQuestions.length) {
      return; // Ensure all questions are answered before proceeding
    }

    const count = Object.keys(answers).reduce((acc, index) => {
      const isCorrect = selectedQuestions[index].answers[answers[index]].isCorrect;
      return isCorrect ? acc + 1 : acc;
    }, 0);

    setTestCompleted(true);
    setCorrectCount(count);
    onShowResult(); // Call the event handler to show the result
  };

  return (
    <div>
      {testCompleted ? (
        <div>
          <h2>Test Results</h2>
          <p>User Score: {correctCount}/{selectedQuestions.length}</p>
        </div>
      ) : (
        <div>
          {selectedQuestions.map((question, index) => (
            <div key={index}>
              <h3>Question {index + 1}</h3>
              <p>{question.question}</p>
              {question.answers.map((answer, answerIndex) => (
                <div key={answerIndex}>
                  <input
                    type="radio"
                    id={`answer-${index}-${answerIndex}`}
                    name={`answer-${index}`}
                    onChange={() => handleAnswerSelect(index, answerIndex)}
                  />
                  <label htmlFor={`answer-${index}-${answerIndex}`}>{answer.answer}</label>
                </div>
              ))}
            </div>
          ))}
          <button onClick={handleNextPage}>Submit answers</button>
        </div>
      )}
    </div>
  );
};

export default Test;
