import React, { useState } from 'react';
import './QuestionSelector.css';

const QuestionSelector = ({ testData, onQuestionsSelect }) => {
  const [selectedAnswers, setSelectedAnswers] = useState(Array(testData.questions.length).fill(null));

  const handleAnswerSelect = (questionIndex, answerIndex) => {
    const newSelectedAnswers = [...selectedAnswers];
    newSelectedAnswers[questionIndex] = answerIndex;
    setSelectedAnswers(newSelectedAnswers);
  };

  const handleQuestionsSubmit = () => {
    onQuestionsSelect(selectedAnswers);
  };

  return (
    <div className="question-selector-container">
      <h2>{testData.testName}</h2>
      {testData.questions.map((question, questionIndex) => (
        <div key={questionIndex} className="question-item">
          <h3>{question.question}</h3>
          {question.answers.map((answer, index) => (
            <div key={index}>
              <input
                type="radio"
                name={`question-${questionIndex}`}
                value={index}
                checked={selectedAnswers[questionIndex] === index}
                onChange={() => handleAnswerSelect(questionIndex, index)}
              />
              <label>{answer.answer}</label>
            </div>
          ))}
        </div>
      ))}
      <button onClick={handleQuestionsSubmit}>Submit Answers</button>
    </div>
  );
};

export default QuestionSelector;
