import React, { useState } from 'react';

const Test = ({ selectedQuestions, onShowResult }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answers, setAnswers] = useState(new Array(selectedQuestions.length).fill(null));

  const handleAnswerSelect = (answerIndex) => {
    const newAnswers = [...answers];
    newAnswers[currentIndex] = answerIndex;
    setAnswers(newAnswers);
  };

  const handleNextQuestion = () => {
    if (currentIndex < selectedQuestions.length - 1) {
      setCurrentIndex(currentIndex + 1);
    } else {
      onShowResult(answers);
    }
  };

  return (
    <div>
      <h2>Question {currentIndex + 1}</h2>
      <p>{selectedQuestions[currentIndex].question}</p>
      {selectedQuestions[currentIndex].answers.map((answer, answerIndex) => (
        <div key={answerIndex}>
          <input
            type="radio"
            id={`answer-${answerIndex}`}
            name="answer"
            value={answerIndex}
            checked={answers[currentIndex] === answerIndex}
            onChange={() => handleAnswerSelect(answerIndex)}
          />
          <label htmlFor={`answer-${answerIndex}`}>{answer.answer}</label>
        </div>
      ))}
      <button onClick={handleNextQuestion}>Next Question</button>
    </div>
  );
};

export default Test;
