import React, { useState } from 'react';
import QuestionSelector from './QuestionSelector';
import Test from './Test';

const testData = {
  testName: "Hiking and Trekking Test",
  questions: [
    {
      question: "What is the most essential item to bring on a hike?",
      answers: [
        { answer: "Water", isCorrect: true },
        { answer: "Snacks", isCorrect: false },
        { answer: "Map", isCorrect: false },
        { answer: "Camera", isCorrect: false },
      ],
    },
    // Add more questions here
  ],
};

const App = () => {
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [showResult, setShowResult] = useState(false);
  const [userScore, setUserScore] = useState(null);

  const handleQuestionsSelect = (selectedAnswers) => {
    setSelectedQuestions(selectedAnswers);
    setShowResult(true);
  };

  const handleFinish = (score) => {
    setUserScore(score);
    setShowResult(true);
  };

  return (
    <div className="App">
      {!showResult ? (
        <QuestionSelector testData={testData} onQuestionsSelect={handleQuestionsSelect} />
      ) : (
        <Test selectedQuestions={selectedQuestions} onFinish={handleFinish} />
      )}
      {showResult && (
        <div>
          <h2>Test Results</h2>
          <p>User Score: {userScore}/{selectedQuestions.length}</p>
        </div>
      )}
    </div>
  );
};

export default App;
