import React, { useState } from 'react';
import './QuestionSelector.css'; 

const QuestionSelector = ({ testData, onQuestionsSelect, answeredQuestions, onShowAllResults }) => {
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [showWarning, setShowWarning] = useState(false);

  const handleQuestionToggle = (questionIndex) => {
    const updatedQuestions = [...selectedQuestions];
    updatedQuestions[questionIndex] = !updatedQuestions[questionIndex];
    setSelectedQuestions(updatedQuestions);
  };

  const handleStartTest = () => {
    const questionsToSend = testData.questions.filter((question, index) => selectedQuestions[index]);
    if (questionsToSend.length === 0) {
      setShowWarning(true);
    } else {
      setShowWarning(false);
      onQuestionsSelect(questionsToSend);
    }
  };

  return (
    <div className="question-selector-container">
      <h2 className="question-selector-header">Choose questions:</h2>
      {showWarning && <p className="warning">Please select at least one question.</p>}
      {testData.questions.map((question, index) => (
        <div key={index} className={`question-item ${answeredQuestions.includes(question.question) ? 'answered' : ''}`}>
          <input
            type="checkbox"
            checked={selectedQuestions[index] || false}
            onChange={() => handleQuestionToggle(index)}
            className="question-checkbox"
            disabled={answeredQuestions.includes(question.question)}
          />
          <label>{question.question}</label>
        </div>
      ))}
      {answeredQuestions.length < testData.questions.length ? (
        <button onClick={handleStartTest} className="start-test-button">Start Quiz</button>
      ) : (
        <button onClick={onShowAllResults} className="start-test-button">Show All Results</button>
      )}
    </div>
  );
};

export default QuestionSelector;
