import React, { useState } from 'react';

const QuestionSelector = ({ testData, onQuestionsSelect, onStartQuiz, errorMessage }) => {
  const [selectedQuestions, setSelectedQuestions] = useState([]);

  const handleCheckboxChange = (question) => {
    setSelectedQuestions((prevSelectedQuestions) => {
      if (prevSelectedQuestions.includes(question)) {
        return prevSelectedQuestions.filter((q) => q !== question);
      } else {
        return [...prevSelectedQuestions, question];
      }
    });
  };

  const handleStartQuiz = () => {
    if (selectedQuestions.length === 0) {
      onStartQuiz();
    } else {
      onQuestionsSelect(selectedQuestions);
    }
  };

  return (
    <div className="question-selector-container">
      <div className="question-selector-header">Select Your Questions</div>
      {testData.questions.map((question, index) => (
        <div className="question-item-container" key={index}>
          <input
            type="checkbox"
            className="question-checkbox"
            onChange={() => handleCheckboxChange(question)}
          />
          <label className="question-item">{question.question}</label>
        </div>
      ))}
      {errorMessage && <div className="warning-message">{errorMessage}</div>}
      <button className="start-test-button" onClick={handleStartQuiz}>
        Start Quiz
      </button>
    </div>
  );
};

export default QuestionSelector;
