import React, { useState } from 'react';

const Test = ({ selectedQuestions }) => {
  const [correctAnswersCount, setCorrectAnswersCount] = useState(0);
  const [testCompleted, setTestCompleted] = useState(false);

  const handleFinish = () => {
    const correctCount = Object.keys(selectedQuestions).reduce((acc, index) => {
      const questionIndex = parseInt(index);
      const isCorrect = selectedQuestions[questionIndex].answers[selectedQuestions[index]].isCorrect;
      return isCorrect ? acc + 1 : acc;
    }, 0);
    setCorrectAnswersCount(correctCount);
    setTestCompleted(true);
  };

  return (
    <div>
      {testCompleted && (
        <div>
          <h2>Test Results</h2>
          <p>User Score: {correctAnswersCount}/{Object.keys(selectedQuestions).length}</p>
        </div>
      )}
    </div>
  );
};

export default Test;
