import React, { useState } from 'react';
import './QuestionSelector.css';

const QuestionSelector = ({ testData, onQuestionsSelect }) => {
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [showStartButton, setShowStartButton] = useState(true);

  const handleQuestionToggle = (questionIndex) => {
    const updatedQuestions = [...selectedQuestions];
    updatedQuestions[questionIndex] = !updatedQuestions[questionIndex];
    setSelectedQuestions(updatedQuestions);
  };

  const handleStartTest = () => {
    setShowStartButton(false);
  };

  const handleNextQuestion = () => {
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const handleQuestionsSubmit = () => {
    const questionsToSend = testData.questions.filter((question, index) => selectedQuestions[index]);
    onQuestionsSelect(questionsToSend);
  };

  return (
    <div className="question-selector-container">
      {showStartButton && (
        <button onClick={handleStartTest} className="start-test-button">Start Quiz</button>
      )}

      {currentQuestionIndex < testData.questions.length && !showStartButton && (
        <>
          <h2 className="question-selector-header">{testData.questions[currentQuestionIndex].question}</h2>
          {testData.questions[currentQuestionIndex].answers.map((answer, index) => (
            <div key={index} className="question-item">
              <input
                type="radio"
                id={`answer${index}`}
                name="answers"
                value={index}
                checked={selectedQuestions[currentQuestionIndex] === index}
                onChange={() => handleQuestionToggle(currentQuestionIndex)}
                className="question-checkbox"
              />
              <label htmlFor={`answer${index}`}>{answer.answer}</label>
            </div>
          ))}
          <button onClick={handleNextQuestion} className="next-question-button">Next Question</button>
        </>
      )}

      {currentQuestionIndex === testData.questions.length && !showStartButton && (
        <button onClick={handleQuestionsSubmit} className="submit-questions-button">Submit Questions</button>
      )}
    </div>
  );
};

export default QuestionSelector;
