import React, { useState } from 'react';
import QuestionSelector from './QuestionSelector';
import Test from './Test';

const testData = {
  testName: "Hiking and Trekking Test",
  questions: [
    {
      question: "What is the most essential item to bring on a hike?",
      answers: [
        {
          answer: "Water",
          isCorrect: true,
        },
        {
          answer: "Snacks",
          isCorrect: false,
        },
        {
          answer: "Map",
          isCorrect: false,
        },
        {
          answer: "Camera",
          isCorrect: false,
        }
      ],
    },
    {
      question: "Which of these mountains is the highest in the world?",
      answers: [
        {
          answer: "Mount Everest",
          isCorrect: true,
        },
        {
          answer: "K2",
          isCorrect: false,
        },
        {
          answer: "Kangchenjunga",
          isCorrect: false,
        },
        {
          answer: "Lhotse",
          isCorrect: false,
        }
      ],
    },
    {
      question: "What should you do if you encounter a bear during a hike?",
      answers: [
        {
          answer: "Stay calm and slowly back away",
          isCorrect: true,
        },
        {
          answer: "Run away as fast as you can",
          isCorrect: false,
        },
        {
          answer: "Throw rocks at the bear",
          isCorrect: false,
        },
        {
          answer: "Play dead",
          isCorrect: false,
        }
      ],
    },
    {
      question: "Which of the following is a popular trekking destination in Nepal?",
      answers: [
        {
          answer: "Annapurna Circuit",
          isCorrect: true,
        },
        {
          answer: "Inca Trail",
          isCorrect: false,
        },
        {
          answer: "Tour du Mont Blanc",
          isCorrect: false,
        },
        {
          answer: "Pacific Crest Trail",
          isCorrect: false,
        }
      ],
    },
    {
      question: "What is the primary purpose of a trekking pole?",
      answers: [
        {
          answer: "To provide stability and reduce strain on joints",
          isCorrect: true,
        },
        {
          answer: "To ward off wild animals",
          isCorrect: false,
        },
        {
          answer: "To test the depth of water crossings",
          isCorrect: false,
        },
        {
          answer: "To mark the trail",
          isCorrect: false,
        }
      ],
    },
  ],
};

const App = () => {
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [showResult, setShowResult] = useState(false);
  const [correctAnswersCount, setCorrectAnswersCount] = useState(0); // Додано correctAnswersCount

  const handleQuestionsSelect = (selectedQuestions) => {
    setSelectedQuestions(selectedQuestions);
    setShowResult(false);
  };

  const handleFinish = (count) => { // Передавайте кількість правильних відповідей для встановлення correctAnswersCount
    setCorrectAnswersCount(count);
    setShowResult(true);
  };

  return (
    <div className="App">
      {selectedQuestions.length === 0 ? (
        <QuestionSelector testData={testData} onQuestionsSelect={handleQuestionsSelect} />
      ) : (
        <Test selectedQuestions={selectedQuestions} onFinish={handleFinish} />
      )}
      {showResult && (
        <div>
          <h2>Test Results</h2>
          <p>User Score: {correctAnswersCount}/{selectedQuestions.length}</p>
        </div>
      )}
    </div>
  );
};

export default App;
